import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams from React Router

const UserDetailComponent = () => {
    const [user, setUser] = useState(null); // Initialize user state to null
    const { id } = useParams(); // Get the user ID from URL parameters

    useEffect(() => {
        axios.get(`http://localhost:8080/api/users/${id}`) // Use the user ID in the API request URL
            .then(response => {
                setUser(response.data);
            })
            .catch(error => {
                console.error('Error fetching User:', error);
            });
    }, [id]); // Add id to the dependency array to trigger effect when it changes

    return (
        <div>
            <h1>User Details</h1>
            {user && (
                <div>
                    <h2>{user.name}</h2>
                                    </div>
            )}
        </div>
    );
}

export default UserDetailComponent;
