import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; 
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const MyProfile = () => {
    const [user, setUser] = useState(null); 
    const { id } = useParams(); 
    const [parameters, setParameters] = useState(false);
   

  const navigate = useNavigate();
  const [cookieValue] = useState(Cookies.get('UserId') || '');

  useEffect(() => {
    const cookieValue = Cookies.get('UserId');
    if (!cookieValue) {
      navigate('/');
    }
  }, [cookieValue, navigate]);

    
    useEffect(() => {
        axios.get(`http://localhost:8080/Auth/show/${id}`)
            .then(response => {
                setUser(response.data);
            })
            .catch(error => {
                console.error('Error fetching User:', error);
            });
    }, [id]); 
    if (!user) {
      return null;
  }
    return (
       
          
            <section className="h-100 gradient-custom-2">
        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-lg-9 col-xl-7">
              <div className="card">
                <div className="rounded-top text-white d-flex flex-row" style={{ backgroundColor: '#000', height: '200px' }}>
                  <div className="ms-4 mt-5 d-flex flex-column" style={{ width: '150px' }}>
                    <img src={user.imgPath}
                         alt="Generic placeholder image" className="img-fluid img-thumbnail mt-4 mb-2"
                         style={{ width: '150px', zIndex: '1' }}/>
                    <button type="button" data-mdb-button-init data-mdb-ripple-init className="btn btn-outline-dark"
                            data-mdb-ripple-color="dark"
                            style={{ zIndex: '1' }} on>
                      Edit profile
                    </button>
                  </div>
                  <div className="ms-3" style={{ marginTop: '130px' }}>
                    <h5>{user.firstName} {user.lastName}</h5>
                    <p>New York</p>
                  </div>
                </div>
                <div className="p-4  text-black" style={{ backgroundColor: '#f8f9fa' }}>
                </div>
                <div className="card-body p-4 text-black">
                  <div className="mb-5">
                    
                    </div>
                  </div>
                  <div className="d-flex justify-content-between align-items-center mb-4">
                    
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    )}


export default MyProfile;
