import React, { useState } from 'react';
import Cookies from 'js-cookie';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/Auth/Login', {
        email,
        password
      });

      console.log('Login successful:', response.data);

      // Assuming the response contains username
      const userData = { username: response.data.email };
      Cookies.set('userData', userData, { expires: 7 });

      // Update the login state to true
      setLoggedIn(true);
    } catch (error) {
      console.error('Login failed:', error);
      // Handle login failure (e.g., display error message to the user)
    }
  };

  return (
    <div>
      {loggedIn ? (
        <Dashboard />
      ) : (
        <div>
          <h2>Login</h2>
          <input
            type="text"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};

const Dashboard = () => {
  const userData = Cookies.get('userData');
  const username = userData ? JSON.parse(userData).username : 'Guest';

  const handleLogout = () => {
    Cookies.remove('userData');
    window.location.reload(); // Refresh the page to reflect logout state
  };

  return (
    <div>
      <h2>Welcome to the Dashboard!</h2>
      <p>You are logged in as {username}</p>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Login;
