import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';


const Login = () => {
  const cookieValue = Cookies.get('UserId');
  if (cookieValue) {
    console.log("Cookie 'mycookie' exists with value:", cookieValue);
  } else {
    setShowPopup(true); 
        setPopupMessage('Email already taken. Please use a different one.');
  }
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null); 
  const [showPopup, setShowPopup] = useState(false); // Define showPopup state
  const [popupMessage, setPopupMessage] = useState(''); // State to store popup message
  const navigate = useNavigate();

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/Auth/login', {
        email,
        password
      });
      if (response.data !== null) {
        setUser(response.data);
        Cookies.set("UserId", user.UserId);
        Cookies.set("UserFN", user.firstName);
        Cookies.set("UserLN", user.lastName);
        Cookies.set("UserEm", user.email);
        Cookies.set("UserIM", user.imgPath);
        navigate('/');
      }
      
    } catch (error) {
      console.error('User:', error);
    }
  };

  return (
    <section className="text-center text-lg-start">
      <div className="card mb-3">
        <div className="row g-0 align-items-center">
          <div className="col-lg-4 d-none d-lg-flex">
            <img
              src="https://mdbootstrap.com/img/new/ecommerce/vertical/004.jpg"
              alt="Trendy Pants and Shoes"
              className="w-100 rounded"
            />
          </div>
          <div className="col-lg-8">
            <div className="card-body py-5 px-md-5">
              <h1 className="mb-4 text-center title mb-3">Login :</h1>
              <form onSubmit={handleSubmit}>
                <div className="form-group mb-4">
                  <label htmlFor="email">Email address</label>
                  <input
                    type="email"
                    id="email"
                    className="form-control"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
                <div className="form-group mb-4">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    id="password"
                    className="form-control"
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>
                <div className="row mb-4">
                  <div className="col-md-6">
                    <div className="form-group">
                    <button
                        type="submit"
                        className="btn btn-primary btn-block mb-4">
                        Login
                        </button>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                    You don't have an account
                        <a href='/Auth/Register' className=''> Register?</a>
                    </div>
                  </div>
                  {showPopup && (
                  <div className="alert alert-danger" role="alert">
                    {popupMessage}
                  </div>
                )}
                </div>
                
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
