import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [imgFile, setImgFile] = useState(null); // Store the image file
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('file', imgFile); // Append image file to FormData
      formData.append('user', JSON.stringify({
        idU: '',
        firstName,
        lastName,
        email,
        password,
        imgPath: '' // Leave it empty as it will be filled by the backend
      }));

      const response = await axios.post('http://localhost:8080/Auth/save', formData, {
        headers: {
          'Content-Type': 'multipart/form-data' // Set the content type to multipart/form-data
        }
      });

      if (response.data === "Image already exist" || response.data === "Email Already Exist" || response.data === "Error") {
        setShowPopup(true);
        setPopupMessage(response.data);
      } else {
        Cookies.set("UserId", response.data);
        Cookies.set("UserFN", firstName);
        Cookies.set("UserLN", lastName);
        Cookies.set("UserEm", email);
        // Cookies.set("UserIM", imgPath); // No need to set this, as it's handled by the backend
        navigate('/');
      }
    } catch (error) {
      console.error('User:', error.message, 'test');
      setShowPopup(true);
      setPopupMessage('Connection Error');
    }
  };

  const handleFileChange = (e) => {
    setImgFile(e.target.files[0]); // Store the selected image file
  };

  return (
    <section className="text-center text-lg-start">
      <div className="card mb-3">
        <div className="row g-0 align-items-center">
          {/* Other HTML content remains the same */}
          <div className="form-group mb-4">
            <label htmlFor="imgPath">Image Path</label>
            <input
              type="file" // Change input type to file
              id="imgPath"
              className="form-control"
              onChange={handleFileChange} // Handle file change event
            />
          </div>
          {/* Other HTML content remains the same */}
        </div>
      </div>
    </section>
  );
};

export default Register;
