import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

const Navbar = () => {
    const navigate = useNavigate();
    const [userId, setUserId] = useState('');
    const [userFullName, setUserFullName] = useState('');

    useEffect(() => {
        const userIdFromCookie = Cookies.get('UserId');
        const userFirstName = Cookies.get('UserFN');
        const userLastName = Cookies.get('UserLN');

        if (userIdFromCookie) {
            setUserId(userIdFromCookie);
            setUserFullName(`${userFirstName} ${userLastName}`);
        }
    }, []);

    const handleLogout = () => {
        Cookies.remove('UserId');
        Cookies.remove('UserFN');
        Cookies.remove('UserLN');
        setUserId('');
        setUserFullName('');
    };

    const handleLogin = () => {
        navigate('/Auth/Login');
    };

    return (
        <nav className="row navbar px-5 shadow-sm bg-body-tertiary fixed-top">
            <div className="container-fluid">
                <a className="navbar-brand" href="/"><img style={{width: '8em'}} src="/Images/logoBlack.png" alt="Logo"/></a>
                <div className="row justify-content-end">
                    <div className="col-auto">
                        <a type="button" className="btn text-decoration-none btn-light">Host on Comfy Rental</a>
                    </div>
                    <div className="col-auto">
                        <div className="dropdown-center">
                            <button style={{width: '3em', height: '2.2em'}} className="btn btn-outline-dark dropdown-toggler" type="button" data-bs-toggle="dropdown">
                                <span style={{width: '3em', height: '2.2em'}} className="bi bi-person-lines-fill"></span>
                            </button>
                            <ul className="dropdown-menu dropdown-menu-end dropdown-menu-lg-star">
                                {userId ? (
                                    <React.Fragment>
                                        <li><a href={`/Profile/${userId}`} className="dropdown-item">{userFullName}</a></li>
                                        <li><a onClick={handleLogout} className="dropdown-item">Logout</a></li>
                                    </React.Fragment>
                                ) : (
                                    <React.Fragment>
                                        <li><a className="dropdown-item" href="/Auth/Register">Register</a></li>
                                        <li><a onClick={handleLogin} className="dropdown-item">Login</a></li>
                                    </React.Fragment> 
                                )}
                                <hr/>
                                <li><a className="dropdown-item" href="#">Host on Comfy Rental</a></li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
