import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [imgFile, setImgFile] = useState(null); // Store file object instead of path
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const navigate = useNavigate();
  const [cookieValue] = useState(Cookies.get('UserId') || '');

  useEffect(() => {
    const cookieValue = Cookies.get('UserId');
    if (cookieValue) {
      console.log("Cookie 'UserId' exists with value:", cookieValue);
      navigate('/');
    }
  }, [cookieValue]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImgFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('firstName', firstName);
      formData.append('lastName', lastName);
      formData.append('email', email);
      formData.append('password', password);
      formData.append('imgFile', imgFile); // Append the file object

      const response = await axios.post('http://localhost:8080/Auth/save', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Set content type as form data
        },
      });

      if (response.data === "Image already exist" || response.data === "Email Already Exist" || response.data === "Error") {
        setShowPopup(true);
        setPopupMessage(response.data);
      } else {
        Cookies.set("UserId", response.data);
        Cookies.set("UserFN", firstName);
        Cookies.set("UserLN", lastName);
        Cookies.set("UserEm", email);
        Cookies.set("UserIM", response.data.imgPath); // Assuming backend returns the image path
        navigate('/');
      }
    } catch (error) {
      console.error('User:', error.message);
      setShowPopup(true);
      setPopupMessage('Connection Error');
    }
  };

  return (
    <section className="text-center text-lg-start">
      <div className="card mb-3">
        <div className="row g-0 align-items-center">
          <div className="col-lg-4 d-none d-lg-flex">
            <img
              src="https://mdbootstrap.com/img/new/ecommerce/vertical/004.jpg"
              alt="Trendy Pants and Shoes"
              className="w-100 rounded"
            />
          </div>
          <div className="col-lg-8">
            <div className="card-body py-5 px-md-5">
              <h1 className="mb-4 text-center title mb-3">Register</h1>
              <form onSubmit={handleSubmit}>
                {/* Form fields */}
                <div className="form-group mb-4">
                  <label htmlFor="imgFile">Upload Image</label>
                  <input
                    type="file"
                    id="imgFile"
                    className="form-control"
                    onChange={handleFileChange}
                    required
                  />
                </div>
                {/* Form submission */}
                <button type="submit" className="btn btn-primary btn-block mb-4">
                  Register
                </button>
                {/* Error popup */}
                {showPopup && (
                  <div className="alert alert-danger" role="alert">
                    {popupMessage}
                  </div>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
