import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import CreateUserForm from './Components/CreateUserForm';
import UserDetailComponent from './Components/UserDetailComponent';
import UserComponent from './Components/UserComponent';
import Register from './Components/Auth/Register';
import Login from './Components/Auth/Login';
import Cookies from 'js-cookie'; // Import Cookies

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(Cookies.get('UserId') !== undefined);

  useEffect(() => {
    const userId = Cookies.get('UserId');
    setIsLoggedIn(userId !== undefined);
  }, []);

  const handleLogout = () => {
    Cookies.remove('UserId');
    setIsLoggedIn(false);
    return <Navigate to="/Auth/Login" />;
  };

  return (
    <Router>
      <Routes>
        <Route path="/User/:id" element={<UserDetailComponent />} />
        <Route path="/User" element={<UserComponent />} />
        <Route path="/CreateUser" element={<CreateUserForm />} />

        {/* Guard for Login route based on isLoggedIn state */}
        {isLoggedIn ? (
          <Route path="/Auth/Login" element={<button onClick={handleLogout}>Logout</button>} />
        ) : (
          <Route path="/Auth/Login" element={<Login />} />
        )}

        <Route path="/Auth/Register" element={<Register />} />
      </Routes>
    </Router>
  );
};

export default App;
