import React from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import CreateUserForm from './Components/CreateUserForm';
import UserDetailComponent from './Components/UserDetailComponent';
import UserComponent from './Components/UserComponent';
import Register from './Components/Auth/Register';
import Login from './Components/Auth/Login';
import Cookies from 'js-cookie'; // Import Cookies

const App = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    Cookies.remove('UserId'); // Remove UserId cookie
    Cookies.remove('UserFN'); // Remove other user cookies (optional)
    Cookies.remove('UserLN');
    Cookies.remove('UserEm');
    Cookies.remove('UserIM');
    navigate('/Auth/Login'); // Redirect to Login page after logout
  };

  return (
    <Router>
      <Routes>
        <Route path="/User/:id" element={<UserDetailComponent />} />
        <Route path="/User" element={<UserComponent />} />
        <Route path="/CreateUser" element={<CreateUserForm />} />
        <Route path="/Auth/Register" element={<Register />} />
        <Route path="/Auth/Login" element={<Login />} />

        <Route
          path="/"
          element={
            // Check for cookie and conditionally render content
            Cookies.get('UserId') !== undefined ? (
              <div>
                <h1>Welcome back!</h1>
                <button onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <p>Please log in or register to access this page</p>
            )
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
