import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
    // State variables to store username, password, and message
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    // Function to handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault();
        
        try {
            const response = await axios.post('/api/users', {  // Assuming your endpoint for user registration is '/api/users'
                username: username,
                password: password
            });

            setMessage('User registered successfully');
            setUsername(''); // Clearing username input field
            setPassword(''); // Clearing password input field
        } catch (error) {
            setMessage('Error registering user');
        }
    };

    return (
        <div>
            <h2>User Registration</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                
                <button type="submit">Register</button>
            </form>
            
            <p>{message}</p>
        </div>
    );
};

export default RegistrationForm;
