import React from 'react';
import { Carousel } from 'react-bootstrap';
import image1 from './Images/image1.jpg';
import image2 from './Images/image2.jpg';
import image3 from './Images/image3.jpg';
import image4 from './Images/image4.jpg';
import './LocalDetails.css'; 
import DatePicker from "react-datepicker"
import "react-datepicker/src/stylesheets/datepicker.css";

class LocalDetails extends React.Component {
    render() {
        const images = [image1, image2, image3, image4];

        return (
            <div>
                <div className="title-container">
                    <div>
                        <h1 className="title">Logement entier : hébergement - Larbaa Beni Hassen, Maroc</h1>
                        <h6>6 voyageurs 3 chambres 5 lits 2 salles de bain</h6>
                    </div>
                </div>
                <div className="carousel-wrapper">
                    <div className="carousel-container">
                        <Carousel>
                            {images.map((image, index) => (
                                <Carousel.Item key={index}>
                                    <img
                                        className="d-block w-100 rounded-carousel-image"
                                        src={image}
                                        alt=""
                                    />
                                </Carousel.Item>
                            ))}
                        </Carousel>
                    </div>
                    <div className="info-container">
                        <div>
                            <p>120 €</p>
                            <p>par nuit</p>
                        </div>
                        <div>
                        <p>ARRIVÉE</p>
                            <DatePicker
                                selected={this.state.arrivalDate}
                                onChange={date => this.setState({ arrivalDate: date })}
                                placeholderText="Select date"
                            />
                            <p>DÉPART</p>
                            <DatePicker
                                selected={this.state.departureDate}
                                onChange={date => this.setState({ departureDate: date })}
                                placeholderText="Select date"
                            />
    <p>VOYAGEURS</p>
    <p>1 voyageur</p>
</div>
                                <div>
                                    <button>Réserver</button>
                                    <p>Aucun montant ne vous sera débité pour le moment</p>
                                </div>
                                <div>
                                    <p>120 € x 5 nuits</p>
                                    <button>Afficher le détail du prix</button>
                                    <p>600 €</p>
                                </div>
                                <div>
                                    <p>Frais de service Airbnb</p>
                                    <button>Afficher le détail du prix</button>
                                    <p>102 €</p>
                                </div>
                                <div>
                                    <p>Total</p>
                                    <p>702 €</p>
                                </div>
                        </div>
                    </div>
                </div >
                );

    }
}

                export default LocalDetails;
