import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; 
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';
import BookingComponent from '../Reservation/Booking';

const MyProfile = () => {
    const [user, setUser] = useState(null); 
    const { id } = useParams(); 
    const [parameters, setParameters] = useState(false);
    const navigate = useNavigate();
    const cookieValue = Cookies.get('UserId') || '';
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [password, setPassword] = useState('');
    const [myBookings, setMyBookings] = useState(false);
    const [myHostings, setMyHostings] = useState(false);

    useEffect(() => {
        if (!cookieValue) {
            navigate('/');
        } else {
            navigate(`/Profile/${cookieValue}`);
        }
    }, [cookieValue, navigate]);

    useEffect(() => {
        axios.get(`http://localhost:8080/Auth/show/${id}`)
            .then(response => {
                setUser(response.data);
            })
            .catch(error => {
                console.error('Error fetching User:', error);
            });
    }, [id]);

    const handleEditProfile = () => {
        setParameters(true);
    };
    const handleMyBookings = () => {
        setMyBookings(true);
    };
    const handleMyHostings = () => {
        setMyHostings(true);
    };
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.put(`http://localhost:8080/Auth/Update/${id}`, {
                idU: '',
                firstName,
                lastName,
                password,
            });
            setUser(response.data);
            console.log("chnu kayn comme data"+response.data);
            setParameters(false); 
            setFirstName('');
            setLastName('');
            setPassword(''); 
        } catch (error) {
            console.error('Error updating profile:', error);
        }
    };

    const handleCancel = () => {
        setParameters(false);
    };
    const handleHideB = () => {
        setMyBookings(false);
    };
    const handleHideH = () => {
        setMyH(false);
    };
    if (!user) {
        return null;
    }

    return (
        <section className="h-100 gradient-custom-2">
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col col-lg-9 col-xl-7">
                        <div className="card">
                            <div className="rounded-top text-white d-flex flex-row" style={{ backgroundColor: '#000', height: '200px' }}>
                                <div className="ms-4 mt-5 d-flex flex-column" style={{ width: '150px' }}>
                                    <img src={user.imgPath} alt="Generic placeholder image" className="img-fluid img-thumbnail mt-4 mb-2" style={{ width: '150px', zIndex: '1' }}/>
                                    
                                </div>
                                <div className="ms-3" style={{ marginTop: '130px' }}>
                                    <h5>{user.firstName} {user.lastName}</h5>
                                    <p>New York</p>
                                </div>
                            </div>
                            <div className="p-4  text-black" style={{ backgroundColor: '#f8f9fa' }}>
                            <button type="button" data-mdb-button-init data-mdb-ripple-init className="btn btn-outline-dark" data-mdb-ripple-color="dark" style={{ zIndex: '1' }} onClick={handleEditProfile}>
                                        Edit profile
                                    </button>
                                {parameters ? (
                                    <form onSubmit={handleSubmit}>
                                        <div className="form-group mb-4">
                                            <label htmlFor="firstName">Change First Name</label>
                                            <input
                                                type="text"
                                                id="firstName"
                                                className="form-control"
                                                required
                                                value={firstName}
                                                onChange={(e) => setFirstName(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group mb-4">
                                            <label htmlFor="lastName">Change Last Name</label>
                                            <input
                                                type="text"
                                                id="lastName"
                                                className="form-control"
                                                required
                                                value={lastName}
                                                onChange={(e) => setLastName(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group mb-4">
                                            <label htmlFor="password">Change Password</label>
                                            <input
                                                type="password"
                                                id="password"
                                                className="form-control"
                                                required
                                                value={password}
                                                onChange={(e) => setPassword(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group">
                                            <button
                                                type="submit"
                                                className="btn btn-primary me-2">
                                                Submit
                                            </button>
                                            <button
                                                type="button"
                                                className="btn btn-secondary"
                                                onClick={handleCancel}>
                                                Cancel
                                            </button>
                                        </div>
                                    </form>
                                ) : null}
                            </div>
                                
                                
                            </div>
                            <div className="p-4  text-black" style={{ backgroundColor: '#f8f9fa' }}>
                            <button type="button" data-mdb-button-init data-mdb-ripple-init className="btn btn-outline-dark" data-mdb-ripple-color="dark" style={{ zIndex: '1' }} onClick={handleMyBookings}>
                                        My Bookings
                                    </button>

                                    {myBookings ? (

                                    <form onSubmit={handleSubmit}>
                                        <div className="form-group mb-4">
                                            <label htmlFor="firstName">Change First Name</label>
                                            <input
                                                type="text"
                                                id="firstName"
                                                className="form-control"
                                                required
                                                value={firstName}
                                                onChange={(e) => setFirstName(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group mb-4">
                                            <label htmlFor="lastName">Change Last Name</label>
                                            <input
                                                type="text"
                                                id="lastName"
                                                className="form-control"
                                                required
                                                value={lastName}
                                                onChange={(e) => setLastName(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group mb-4">
                                            <label htmlFor="password">Change Password</label>
                                            <input
                                                type="password"
                                                id="password"
                                                className="form-control"
                                                required
                                                value={password}
                                                onChange={(e) => setPassword(e.target.value)}
                                            />
                                        </div>
                                        <div className="form-group">
                                           
                                            <button
                                                type="button"
                                                className="btn btn-secondary"
                                                onClick={handleCancel}>
                                                Hide
                                            </button>
                                        </div>
                                    </form>
                                ) : null}

                            </div>
                        </div>
                    </div>
                </div>
                 <BookingComponent></BookingComponent>
        </section>
    );
}

export default MyProfile;
