import React, { useState } from 'react';
import Cookies from 'js-cookie';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory hook for navigation

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory(); // Initialize useHistory hook

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/Auth/Login', {
        email,
        password
      });

      console.log('Login successful:', response.data);

      // Assuming the response contains username
      const userData = { username: response.data.email };
      Cookies.set('userData', userData, { expires: 7 });

      // Redirect to a different route upon successful login
      history.push('/dashboard'); // Redirect to /dashboard route
    } catch (error) {
      console.error('Login failed:', error);
      // Handle login failure (e.g., display error message to the user)
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
